-- step 1: create a database
create database if not exists sql_case_study;
use sql_case_study;

-- step 2: create all the tables: a.customer_profile, b.product_inventory, c. sales_transaction
-- tables sql commmand is generated by cmd: csvsql --dialect mysql --snifflimit 10000 sales_transaction.csv>sales_.sql
-- tables sql commmand is generated by cmd: csvsql --dialect mysql --snifflimit 10000 product_inventory.csv>product.sql
-- tables sql commmand is generated by cmd: csvsql --dialect mysql --snifflimit 10000 customer_profiles.csv>customer.sql

SHOW VARIABLES LIKE 'secure_file_priv';
CREATE TABLE customer_profiles (
	`CustomerID` DECIMAL(38, 0) NOT NULL, 
	`Age` DECIMAL(38, 0) NOT NULL, 
	`Gender` VARCHAR(6) NOT NULL, 
	`Location` VARCHAR(7) NOT NULL, 
	`JoinDate` DATE NOT NULL
);
CREATE TABLE product_inventory (
	`ProductID` DECIMAL(38, 0) NOT NULL, 
	`ProductName` VARCHAR(11) NOT NULL, 
	`Category` VARCHAR(15) NOT NULL, 
	`StockLevel` DECIMAL(38, 0) NOT NULL, 
	`Price` DECIMAL(38, 2) NOT NULL
);
CREATE TABLE sales_transaction (
	`TransactionID` DECIMAL(38, 0) NOT NULL, 
	`CustomerID` DECIMAL(38, 0) NOT NULL, 
	`ProductID` DECIMAL(38, 0) NOT NULL, 
	`QuantityPurchased` DECIMAL(38, 0) NOT NULL, 
	`TransactionDate` DATE NOT NULL, 
	`Price` DECIMAL(38, 2) NOT NULL
);

desc customer_profiles;
