-- Top 10 Products by Total Sales in terms of total money spent
SELECT
    p.ProductName,
    SUM(st.QuantityPurchased * st.Price) AS TotalSales
FROM
    sales_transaction st
JOIN
    product_inventory p ON st.ProductID = p.ProductID
GROUP BY
    p.ProductName
ORDER BY
    TotalSales DESC
LIMIT 10;
