-- Sales Trends(Monthly Sales):  TransactionDate(YYYY-MM-DD)
SELECT
    DATE_FORMAT(TransactionDate, '%c-%Y') AS SalesMonth, -- %b for months name like jan can be done like %c(numeric 1,2, 10)
    SUM(QuantityPurchased * Price) AS MonthlySales
FROM
    sales_transaction
GROUP BY
    SalesMonth
ORDER BY
    SalesMonth;
