-- month over month sale growth
SELECT
    cp.CustomerID,
    COUNT(st.TransactionID) AS TotalOrders,
    CASE
        WHEN COUNT(st.TransactionID) = 0 THEN 'No orders'
        WHEN COUNT(st.TransactionID) BETWEEN 1 AND 10 THEN 'Low'
        WHEN COUNT(st.TransactionID) BETWEEN 11 AND 30 THEN 'Mid'
        WHEN COUNT(st.TransactionID) > 30 THEN 'High Value'
        ELSE 'Unknown'
    END AS CustomerSegment_ByOrders
FROM
    customer_profiles cp
LEFT JOIN
    sales_transaction st ON cp.CustomerID = st.CustomerID
GROUP BY
    cp.CustomerID
ORDER BY
    TotalOrders DESC;
