-- segment customer based on total spending
WITH CustomerSpending AS (
    SELECT
        CustomerID,
        SUM(QuantityPurchased * Price) AS TotalSpending
    FROM
        sales_transaction
    GROUP BY
        CustomerID
)
SELECT
    cp.CustomerID,
    COALESCE(cs.TotalSpending, 0) AS TotalSpending,
    CASE
        WHEN COALESCE(cs.TotalSpending, 0) = 0 THEN 'No Spending'
        WHEN COALESCE(cs.TotalSpending, 0) < 500 THEN 'Low Spender'
        WHEN COALESCE(cs.TotalSpending, 0) BETWEEN 500 AND 2000 THEN 'Mid Spender'
        WHEN COALESCE(cs.TotalSpending, 0) > 2000 THEN 'High Spender'
        ELSE 'Unknown'
    END AS CustomerSegment_BySpending
FROM
    customer_profiles cp
LEFT JOIN
    CustomerSpending cs ON cp.CustomerID = cs.CustomerID
ORDER BY
    TotalSpending DESC;
    
